function PostPage(props) {
    
    const location=useLocation();
    console.log(location.state.post);
    return (
        <div className="timeline">
            <Navbar />
            <Grid item sm={6} xs={12}>
                <Post id={location.state.id} />
            </Grid>
            <Grid item sm={6} xs={12}>
                Replies
            </Grid>
        </div>
    )
}

