import React from 'react'
import {connect} from 'react-redux'
import CardHeader from '@material-ui/core/CardHeader';
import CardMedia from '@material-ui/core/CardMedia';
import CardContent from '@material-ui/core/CardContent';
import CardActions from '@material-ui/core/CardActions';
import Avatar from '@material-ui/core/Avatar';
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';
import FavoriteBorderOutlinedIcon from '@material-ui/icons/FavoriteBorderOutlined';
import FavoriteOutlinedIcon from '@material-ui/icons/FavoriteOutlined';
import QuestionAnswerOutlinedIcon from '@material-ui/icons/QuestionAnswerOutlined';
import MoreVertIcon from '@material-ui/icons/MoreVert';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import moment from 'moment'
import Grid from '@material-ui/core/Grid'

const useStyles = makeStyles((theme) => ({
    root: {
      maxWidth: 900,
      marginTop: '20%',
      marginLeft: 20,
      textAlign:'left'
    },
    rootExpand: {
        maxWidth: 1000,
        margin: '10%'
    },
    media: {
      maxHeight: 0,
      paddingTop: '56.25%', // 16:9
    },
    mediaExpand: {
      height: 350,
      width: '100%',
      margin: '20% 0%'
    },
    avatar: {
      height: 45,
      width: 45
    },
    content:{
        marginTop: -20
    }
  }));

function RepliesPage(props) {
    const classes = useStyles();
    const handleLike=(e)=>{
        e.preventDefault()
        console.log('Post liked')
    }
    return (
        <div>
            <Grid container spacing={0}>
                <Grid item sm={6} xs={12}>
                    <Card  className={classes.rootExpand}>
                        <CardMedia
                            className={classes.mediaExpand}
                            image={props.post.postImage}
                            title={props.post.body}
                        />
                    </Card>
                </Grid>
                <Grid item sm={6} xs={12}>
                    <Card  className={classes.root}>
                        <CardHeader
                            avatar={
                            <Avatar src={props.post.userImage} alt={props.post.userHandle} aria-label="recipe" className={classes.avatar} />
                            }
                            action={
                            <IconButton aria-label="settings">
                                <MoreVertIcon />
                            </IconButton>
                            }
                            title={props.post.userHandle}
                        />
                        <CardContent className={classes.content}>
                            <Typography variant="body2" color="textSecondary" component="p">
                                {props.post.body}
                            </Typography>
                            <Typography variant="body2" color="textSecondary" component="p">
                                {moment(props.post.createdAt).startOf('hour').fromNow() }
                            </Typography>
                        </CardContent>
                        <CardActions disableSpacing>
                            <IconButton aria-label="add to favorites">
                                <FavoriteBorderOutlinedIcon onClick={handleLike}/>
                            </IconButton>
                            <span>{props.post.likeCount}</span>
                            <IconButton aria-label="share">
                                <QuestionAnswerOutlinedIcon />
                            </IconButton>
                            <span>{props.post.commentCount}</span>
                        </CardActions>
                    </Card>
                </Grid>
            </Grid>
        </div>
    )
}

const mapStateToProps=({data},{id})=>{
    const post = data.posts[id]
    return{
        post: post
    }
}

export default connect(mapStateToProps)(RepliesPage)
