import {SET_ERRORS, CLEAR_ERRORS, LOADING_UI} from '../actionConstants'

const initialState={
    loading: false
}
const uiReducer=(state=initialState,action)=>{
    switch(action.type){
        case LOADING_UI:
            return{
                loading: true
            }
        case SET_ERRORS:
            
        default:
            return state
    }
}

export default uiReducer
