import { GET_POSTS, CREATE_POST, GET_SPECIFIC_POST } from "../actionConstants"

const initialState={
    posts: [],
    specificPost: {}
}

const dataReducer=(state=initialState, action)=>{
    switch(action.type){
        case GET_POSTS:
            return{
                ...state,
                posts: action.data
            }
        case CREATE_POST:
            return{
                ...state,
                posts: action.data
            }
        case GET_SPECIFIC_POST:
            return{
                ...state,
                specificPost: action.data
            }
        default:
            return state
    }
}

export default dataReducer
