import React, {useEffect} from 'react'
import {connect} from 'react-redux'
import Avatar from '@material-ui/core/Avatar';
import Grid from '@material-ui/core/Grid'
import { makeStyles } from '@material-ui/core/styles';
import {getSpecificUser} from '../actions/authUserActions'

const useStyles = makeStyles((theme) => ({
avatar: {
    float: "right",
    height: 100,
    width: 100
    },
}));

function UserProfile(props) {
    const classes = useStyles();

    useEffect(() => {
        props.dispatch(getSpecificUser(props.handle))
        console.log(props.user)        
    }, [props])

    return (
        <div className="timeline">
            { Object.keys(props.user).length===0
            ? ( <p>Loading user...</p> )
            : (
                <Grid container spacing={3} >
                    <Grid item sm={4} xs={12}>
                        <Avatar
                            alt={props.user.handle}
                            src={props.user.imageUrl}
                            className={classes.avatar}
                        />
                    </Grid>
                    <Grid item sm={8} xs={12}>
                        <Typography variant="body2" color="textSecondary" component="p">
                            <span className="userName">{props.post.userHandle}</span> {props.post.body}
                        </Typography>
                        <Typography variant="body2" color="textSecondary" component="p">
                            {moment(props.post.createdAt).startOf('hour').fromNow() }
                        </Typography>
                    </Grid>
                </Grid>
                )
            }
        </div>
    )
}

const mapStateToProps = ({user})=>{
    return{
        user: user.specificUser
    }
}

export default connect(mapStateToProps)(UserProfile)
