import React, {useEffect} from 'react'
import {connect} from 'react-redux'
import Avatar from '@material-ui/core/Avatar';
import { makeStyles } from '@material-ui/core/styles';
import {getSpecificUser} from '../actions/authUserActions'

const useStyles = makeStyles((theme) => ({
avatar: {
    height: 30,
    width: 30
    },
}));

function UserProfile(props) {
    const classes = useStyles();

    useEffect(() => {
        props.dispatch(getSpecificUser(props.handle))
        console.log(props.user.user)        
    }, [props])

    return (
        <div className="timeline">
            {!(Object.keys(props.user.user).length === 0)
            ? ( <p>Loading user...</p> )
            : (
                <Avatar
                    alt={props.user.user.handle}
                    src={props.user.user.userImage}
                    className={classes.avatar}
                />
                )
            }
        </div>
    )
}

const mapStateToProps = ({user})=>{
    return{
        user: user.specificUser
    }
}

export default connect(mapStateToProps)(UserProfile)
