import firebase, { db, firebaseConfig } from '../firebase/firebase';
import {signUpValidate, loginValidate} from '../utils/validators';

import {
    LOGIN_SUCCESS,
    LOGIN_ERROR,
    SIGNOUT_SUCCESS,
    SIGNOUT_ERROR,
    SIGNUP_SUCCESS,
    SIGNUP_ERROR
} from '../actionConstants';
import 'firebase/auth';
import 'firebase/firestore';

function loginSuccess(){
    return{
        type:LOGIN_SUCCESS
    }
}
function loginError(err){
    return{
        type:LOGIN_ERROR,
        err
    }
}
function signoutSuccess(){
    return{
        type:SIGNOUT_SUCCESS
    }
}
function signoutError(err){
    return{
        type:SIGNOUT_ERROR,
        err
    }
}
function signupSuccess(){
    return{
        type:SIGNUP_SUCCESS
    }
}
function signupError(err){
    return{
        type:SIGNUP_ERROR,
        err
    }
}


export const signIn=(credentials)=>{
    return (dispatch, getState, {getFirebase, getFirestore})=>{
        const firebase=getFirebase();
        const firestore=getFirestore()
        const {isValid, errors} = loginValidate(newUser)
        if(!isValid){
            console.log(errors)
        }
        firebase.auth().signInWithEmailAndPassword(
            credentials.email,
            credentials.password
        )
        .then((res)=>{
            console.log(res)
            dispatch(loginSuccess())

            db.collection("users").where('uid','==',res.user.uid).get()
            .then((querySnapshot) => {
                querySnapshot.forEach((doc) =>{
                    console.log(doc.data());
                });
            });
        })
        .catch((err)=>{
            console.log(err)
            dispatch(loginError(err))
        })
    }
}


export const signUp=(newUser)=>{
    return (dispatch, getState, {getFirebase, getFirestore})=>{
        const firebase=getFirebase();
        const firestore=getFirebase().firestore();
        const {isValid, errors} = signUpValidate(newUser)
        if(!isValid){
            console.log(errors)
        }
        let uid;
        const noImage='no-image.png';
        db.collection("users").doc(newUser.userHandle).get()
        .then((doc)=>{
            if(doc.exists){
                console.log('this handle is already taken')
            }
            else{
                return firebase.auth().createUserWithEmailAndPassword(newUser.email,newUser.password)
            }
        })
        .then(data=>{
            uid=data.user.uid;
            console.log(uid)
            const userCredentials={
                handle:newUser.userHandle,
                email:newUser.email,
                createdAt:new Date().toISOString(),
                imageUrl:`https://firebasestorage.googleapis.com/v0/b/${firebaseConfig.storageBucket}/o/${noImage}?alt=media`,
                uid
            }
            return db.doc(`/users/${newUser.userHandle}`).set(userCredentials)
        })
        .then(()=>{
            console.log("User created successfully")
            dispatch(signupSuccess())
        })
        .catch((err)=>{
            dispatch(signupError(err))
            if(err.code === 'auth/email-already-in-use'){
                console.log('Email already in use')
            }
        })
    }
}

