import React, {useEffect} from 'react'
import {connect} from 'react-redux'
import Avatar from '@material-ui/core/Avatar';
import Grid from '@material-ui/core/Grid'
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import {getSpecificUser} from '../actions/authUserActions'

const useStyles = makeStyles((theme) => ({
avatar: {
    marginLeft: "100%",
    marginTop: "8%",
    height: 100,
    width: 100
    },
    handle: { 
        marginTop:'8%',
        fontWeight: '500',
        marginLeft: '-25%',
        fontSize: '20px'
    },
    intro: {
        marginTop: '3%',
    }
}));

function UserProfile(props) {
    const classes = useStyles();

    useEffect(() => {
        props.dispatch(getSpecificUser(props.handle))
        console.log(props.user)        
    }, [])

    return (
        <div className="timeline">
            { Object.keys(props.user).length===0
            ? ( <p>Loading user...</p> )
            : (
                <Grid container spacing={0} >
                    <Grid item sm={4} xs={12}>
                        <Avatar
                            alt={props.user.handle}
                            src={props.user.imageUrl}
                            className={classes.avatar}
                        />
                    </Grid>
                    <Grid item sm={8} xs={12}>
                        <div>
                            <Typography variant="body1" color="textSecondary" component="p" className={classes.handle}>
                                {props.user.handle}
                            </Typography>
                            {props.user.bio && (
                                <Typography variant="body1" color="textPrimary" component="p" className={classes.intro}>
                                    {props.user.bio}
                                </Typography>
                            )}
                        </div>
                    </Grid>
                    {props.user.posts && props.user.posts.map((post)=>(
                        <div>
                            <GridList cellHeight={180} className={classes.gridList}>
                            <GridListTile key="Subheader" cols={2} style={{ height: 'auto' }}>
                                <ListSubheader component="div">December</ListSubheader>
                            </GridListTile>
                            {tileData.map((tile) => (
                            <GridListTile key={tile.img}>
                                <img src={tile.img} alt={tile.title} />
                                <GridListTileBar
                                title={tile.title}
                                subtitle={<span>by: {tile.author}</span>}
                                actionIcon={
                                    <IconButton aria-label={`info about ${tile.title}`} className={classes.icon}>
                                    <InfoIcon />
                                    </IconButton>
                                }
                                />
                            </GridListTile>
                            ))}
                        </GridList>
                        </div>
                    ))}
                </Grid>
                )
            }
        </div>
    )
}

const mapStateToProps = ({user})=>{
    return{
        user: user.specificUser
    }
}

export default connect(mapStateToProps)(UserProfile)
