import React from 'react'
import CardHeader from '@material-ui/core/CardHeader';
import CardMedia from '@material-ui/core/CardMedia';
import CardContent from '@material-ui/core/CardContent';
import CardActions from '@material-ui/core/CardActions';
import Avatar from '@material-ui/core/Avatar';
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';
import { red } from '@material-ui/core/colors';
import FavoriteBorderOutlinedIcon from '@material-ui/icons/FavoriteBorderOutlined';
import FavoriteOutlinedIcon from '@material-ui/icons/FavoriteOutlined';
import QuestionAnswerOutlinedIcon from '@material-ui/icons/QuestionAnswerOutlined';
import MoreVertIcon from '@material-ui/icons/MoreVert';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';

const useStyles = makeStyles((theme) => ({
    root: {
      paddingLeft:50,
      maxWidth: 900,
      marginTop: 20,
      marginLeft: 20,
      textAlign:'left'
    },
    media: {
      maxHeight: 0,
      paddingTop: '56.25%', // 16:9
    },
    avatar: {
      height: 45,
      width: 45
    },
    content:{

    }
  }));

function Post(props) {
    const classes = useStyles();
    return (
        <div>
            <ul className="posts-list">
            {!props.posts
                ? <p>Loading...</p>
                : props.posts.map(post=>(
                    <li key={post.postId}>
                        <Card  className={classes.root}>
                            <CardHeader
                                avatar={
                                <Avatar src={post.userImage} alt={post.userHandle} aria-label="recipe" className={classes.avatar} />
                                }
                                action={
                                <IconButton aria-label="settings">
                                    <MoreVertIcon />
                                </IconButton>
                                }
                                title={post.userHandle}
                                subheader={post.createdAt}
                            />
                            <CardMedia
                                className={classes.media}
                                image="https://firebasestorage.googleapis.com/v0/b/social-app-7ddb9.appspot.com/o/353524666602.jpg?alt=media"
                                title="Paella dish"
                            />
                            <CardActions disableSpacing>
                                <IconButton aria-label="add to favorites">
                                    <FavoriteBorderOutlinedIcon />
                                </IconButton>
                                <span>{post.likeCount}</span>
                                <IconButton aria-label="share">
                                    <QuestionAnswerOutlinedIcon />
                                </IconButton>
                                <span>{post.commentCount}</span>
                            </CardActions>
                            <CardContent className={classes.content}>
                                <Typography variant="body2" color="textSecondary" component="p">
                                {post.body}
                                </Typography>

                            </CardContent>

                        </Card>
                    </li>
                ))
            }
            </ul>
        </div>
    )
}

export default Post
