import React from 'react'
import {storage} from '../firebase/firebase'
import AddAPhotoRoundedIcon from '@material-ui/icons/AddAPhotoRounded';

function ImageForm() {
    const handleImageUpload=(e)=>{
        const image=e.target.files[0]
        const imageExtension = e.target.files[0].name.split('.')[1];
        const imageFileName = `${Math.round(Math.random() * 1000000000000).toString()}.${imageExtension}`;
        console.log(imageFileName)
        // document.querySelector('.addImageText').style.display="none"
        // document.querySelector('#imageText').style.display="inline"
        // document.querySelector('#imageText').innerText=e.target.files[0].name
        const uploadTask=storage.storage.ref(`postImages/${imageFileName}`).put(image);
        uploadTask.on(
            "state_changed",
            snapshot =>{},
            error=>{
                console.log(error)
            },
            storage.ref('postImages').child(imageFileName).getDownloadURL()
            .then(url=>{
                console.log(url)
            })
        )
    }
    return (
        <div className="formDiv">
            <input type="file" style={{visibility: "hidden"}} id="image-icon-file" onChange={handleImageUpload} />
            <label htmlFor="image-icon-file">
                <AddAPhotoRoundedIcon className="addImageBtn" fontSize="large" />
                <p className="addImageText">Click to add image</p>
            </label>

        </div>
    )
}

export default ImageForm
