import {SET_ERRORS, CLEAR_ERRORS, LOADING_UI} from '../actionConstants'

const initialState={
    loading: false,
    errors: null
}
const uiReducer=(state=initialState,action)=>{
    switch(action.type){
        case LOADING_UI:
            return{
                ...state,
                loading: true,
                
            }
        case SET_ERRORS:
            return{
                ...state,
                loading: false,
                errors: action.errors
            }
        case CLEAR_ERRORS:
            return {
                ...state,
                loading: false,
                errors: null
            }
        default:
            return state
    }
}

export default uiReducer
