import {LOGIN_ERROR, LOGIN_SUCCESS, SIGNOUT_SUCCESS, SIGNUP_SUCCESS, SIGNUP_ERROR} from '../actionConstants'

const initialState={
    authenticated: false,
    credentials: {},
    likes: [],
    notifications: []
}

export default function User(state=initialState, action){
    switch(action.type){
        case LOGIN_ERROR:
            console.log('login error')
            return{
                ...state,
                authError: 'Login failed'
            }
        case LOGIN_SUCCESS:
            console.log('login success')
            return{
                ...state,
                authenticated: true
            }
        case SIGNOUT_SUCCESS:
            console.log('signout success')
            return state
        case SIGNUP_SUCCESS:
            console.log('signup success')
            return{
                ...state,
                authError: null
            }
        case SIGNUP_ERROR:
            console.log('signup error')
            return{
                ...state,
                authError: action.err.message
            }
        default:
            return state
    }
}
