import Grid from '@material-ui/core/Grid'

function PostPage() {

    const location=useLocation();
    console.log(props);
    return (
        <div className="timeline">
            <Navbar />
            <Grid container spacing={6}>
                <Grid item sm={6} xs={12}>

                </Grid>
                <Grid item sm={6} xs={12}>
                    Replies
                </Grid>
            </Grid>
        </div>
    )
}

const mapStateToProps=({data})=>{
    return{
        post: data[location.state.id]
