import React, {useState, useEffect} from 'react';
import {connect} from 'react-redux'
import {handleInitialData} from '../actions/dataActions'
import Grid from '@material-ui/core/Grid'
import Post from './post'
import Profile from './profile'

function Timeline(props) {

  useEffect(() => {
    console.log(props)
    props.dispatch(handleInitialData())
  },[])
  console.log(props)
  return (
    <div className="timeline">
      <Grid container spacing={6}>
        <Grid item sm={8} xs={12}>
        {props.posts === undefined
          ? null
          : props.postsIDs.map((id)=>(
                        <li key={id}>
                            <Post id={id}/>
                        </li>
        ))}
        </Grid>
        <Grid item sm={4} xs={12}>
            <Profile />
        </Grid>
      </Grid>
    </div>
  );
}

const mapStateToProps=({data, user})=>{
  const {posts} = data
  console.log(posts)
  if(posts !== undefined){
    const postsIDs = Object.keys(posts)
        .sort((a,b)=>posts[b].createdAt - posts[a].createdAt)
        console.log(postsIDs)
  }
  return{

    authenticated: user.authenticated,
    postsIDs: posts !== undefined ? Object.keys(posts).sort((a,b)=>posts[b].createdAt - posts[a].createdAt) : null
  }
}

export default connect(mapStateToProps)(Timeline)
